// 保持向后兼容性：article.xiaohongshu 引用 moment.xiaohongshu
Object.defineProperty(platforms.article, 'xiaohongshu', {
    get() {
        const info = platforms.moment.xiaohongshu;
        return Object.assign(info, {
            sort: 3,
        });
    },
    
    configurable: true,
    enumerable: true
});

// 平台排序辅助函数，递归处理对象中的平台按 sort 升序
const sortPlatforms = (obj) => {
    // 只对对象类型且不为 null 且非数组进行处理
    if (typeof obj !== 'object' || obj === null || Array.isArray(obj)) return obj;
    // 判断 value 是否为平台对象，即拥有 sort 字段
    const isPlatformObj = Object.values(obj).some(
        (v) => typeof v === 'object' && v !== null && 'sort' in v
    );
    if (!isPlatformObj) {
        // 非平台对象，不需要排序（如顶层对象或无 sort 字段）
        // 递归子级
        let result = {};
        for (const key in obj) {
            result[key] = sortPlatforms(obj[key]);
        }
        return result;
    }
    // 对此层级进行排序
    // 先将平台项排序
    const entries = Object.entries(obj)
        .map(([k, v]) => [k, sortPlatforms(v)]) // 递归处理子对象
        .sort((a, b) => {
            // 如果没 sort 字段依然直接按原序
            let sa = a[1]?.sort ?? 0;
            let sb = b[1]?.sort ?? 0;
            return sa - sb;
        });
    // 构建排序后的对象，保留原有结构
    const sorted = {};
    for (const [k, v] of entries) {
        sorted[k] = v;
    }
    return sorted;
};

export const getPlatforms = () => {
    const data = sortPlatforms(platforms);
    return data;
}