const createPlatform = (type: string, code: string, customProps: Record<string, any> = {}) => {
  const meta = platformMetas[code];
  if (!meta) {
    throw new Error(`Platform meta not found for code: ${code}`);
  }
  return reactive({
    type,
    ...meta,
    ...customProps,
  });
};

